#!/usr/bin/env node
/**
 * FlexiCAD  AI Reference Manifest Builder
 * Scans ai-reference/** for .scad, .md, .json; writes ai-reference/manifest.json
 */
import fs from "fs";
import path from "path";
import crypto from "crypto";
import { fileURLToPath } from "url";

const __filename = fileURLToPath(import.meta.url);
const __dirname = path.dirname(__filename);
const ROOT = path.resolve(__dirname);
const OUT = path.join(ROOT, "manifest.json");
const ALLOWED = new Set([".scad", ".md", ".json"]);

const ts = () => new Date().toISOString();
const log = m => process.stdout.write(`[${ts()}] ${m}\n`);
const sha = b => crypto.createHash("sha256").update(b).digest("hex");

function walk(dir) {
  const out = [];
  for (const e of fs.readdirSync(dir, { withFileTypes: true })) {
    const p = path.join(dir, e.name);
    if (e.isDirectory()) out.push(...walk(p));
    else out.push(p);
  }
  return out;
}

(function main(){
  log("=== Build AI Reference Manifest ===");
  if (!fs.existsSync(ROOT)) {
    log(`ERROR: ai-reference path not found: ${ROOT}`);
    process.exit(1);
  }
  const files = walk(ROOT)
    .filter(p => ALLOWED.has(path.extname(p).toLowerCase()))
    .filter(p => p !== OUT);

  const items = files.map(full => {
    const rel = path.relative(ROOT, full).replace(/\\/g,"/");
    const buf = fs.readFileSync(full);
    const text = buf.toString("utf8");
    return {
      id: sha(Buffer.from(rel)),
      path: rel,
      ext: path.extname(full).slice(1),
      bytes: buf.length,
      sha256: sha(buf),
      // keep full content; well truncate when sampling
      content: text
    };
  });

  const manifest = { version: 1, generated_at: ts(), root: ROOT, count: items.length, items };
  fs.writeFileSync(OUT, JSON.stringify(manifest, null, 2), "utf8");
  log(`Wrote: ${OUT} (${items.length} items)`);
})();

